let num = 0;

const increaseReducer = (state = num, action) => {
  switch (action.type) {
    case "INCREASE_NUM":
      return state + action.paylond;

    case "MULTIPLY_NUM":
      return state * action.paylond;

    case "DECREASE_NUM":
      return state - action.paylond;

    case "DIVIDE_NUM":
      return state / action.paylond;

    default:
      return state;
  }
};

export default increaseReducer;
