import React from 'react'
import {useRouteMatch} from 'react-router-dom'

function About(props) {
    const match=useRouteMatch()
let currentIndex=props.paltar.filter(index=>Number(index.id)===Number(match.params.topicId))

    return (
        <div>
            {
            currentIndex.map((index,key)=>
               <div key={key}>
                   <img src={index.url} />
                   <h1 style={{position:"relative"}}>{index.dressName}</h1>
                   <p style={{display:"inline"}}>{index.price}AZN</p>
               </div>)
            } 
        </div>
    )
}

export default About
