import './App.css';
import { useState,useEffect } from 'react';
import Header from './Header';
import Menu from './Menu';
import Shop from './Shop'
import Contact from './Contact';
import Card from './Card';
import Footer from './Footer';
import About from './About';
import Home from './Home';
import {Switch,Route} from 'react-router-dom'
import axios from 'axios';

function App() {
  const[state,setState]=useState([])

useEffect(()=>{
axios.get("https://my-json-server.typicode.com/emilfermanli/db/product")
.then(res=>setState(res.data))
},[])
  return (
    <div className="App">
     <Header 
     tvt="https://www.createchallenge.org/images/logo-twitter.png/@@images/b588afe1-9051-46e9-b16c-09ba486fcc44.png"
     fb="https://upload.wikimedia.org/wikipedia/commons/thumb/f/fb/Facebook_icon_2013.svg/1024px-Facebook_icon_2013.svg.png"
     tg="https://www.freepnglogos.com/uploads/telegram-logo-png-0.png"
     ig="https://www.freepnglogos.com/uploads/logo-ig-png/logo-ig-instagram-windows-phone-all-you-need-know-9.png"
     background="https://www.addictioncenter.com/app/uploads/2020/01/online_shopping_addiction-scaled.jpeg"
     />
     <Switch>
   <Route path="/">
   <Home paltar={state}/>
   </Route>
   <Route path="/about">

   </Route>
 </Switch>
     <Menu/>
    <Footer
    tvt="https://www.createchallenge.org/images/logo-twitter.png/@@images/b588afe1-9051-46e9-b16c-09ba486fcc44.png"
    fb="https://upload.wikimedia.org/wikipedia/commons/thumb/f/fb/Facebook_icon_2013.svg/1024px-Facebook_icon_2013.svg.png"
    tg="https://www.freepnglogos.com/uploads/telegram-logo-png-0.png"
    ig="https://www.freepnglogos.com/uploads/logo-ig-png/logo-ig-instagram-windows-phone-all-you-need-know-9.png" />

    </div>
  );
}

export default App;
