import './App.css';
import { useState,useEffect } from 'react';
import Header from './Header';
import Menu from './Menu';
import Shop from './Shop'
import Contact from './Contact';
import Card from './Card';
import Footer from './Footer';
import About from './About';
import Home from './Home';
import Offers from './Offers';
import {Switch,Route} from 'react-router-dom'
import axios from 'axios';
import {useSelector,useDispatch} from 'react-redux'


function App() {
  const selector=useSelector(state=>state.increaseReducer)
  const data = useSelector(state => state.increaseReducer)
  const dispatch=useDispatch()
  const[state,setState]=useState([])

useEffect(()=>{
axios.get("https://my-json-server.typicode.com/emilfermanli/db/product")
.then(res=>dispatch({type:"API_DATA",payload:res.data}))
},[dispatch])
  return (
    <div className="App">
      {
        selector
      }
      {
        data.map((index,key)=> <img src="index.url" />)
      }
     <Header  />
     <Switch>
   <Route exact path="/">
   {
        data.map((index,key)=> <img src="index.url" />)
      }
   <Home paltar={state}/>
   </Route>
   <Route path="/offers">
     <Offers />
   </Route>
   <Route path="/:topicId">
     <About paltar={state}/>  
   </Route>
 </Switch>
 <Offers />
     <Menu/>
    <Footer />
    </div>
  );
}

export default App;
